mapper.update:
update t_board_cmt
set iboard = #{iboard},
iuser = #{iuser},
ctnt= #{ctnt}
where iboard_cmt = #{iboard_cmt} and iuser = #{iuser}

mapper.select:
SELECT A.iboard_cmt,A.iboard, A.ctnt, A.created_at, B.uid,B.nm,B.main_pic
FROM t_board_cmt A
INNER JOIN t_user B
ON A.iuser = B.iuser
where iboard = #{iboard}
LIMIT #{startIdx},#{row}

mapper.delete:
DELETE FROM t_board_cmt
WHERE

    iboard_cmt = #{iboard_cmt}
    AND iuser = #{iuser}


    iboard = #{iboard}
